import React, { Component } from 'react';
import NavBar from "../components/NavBar";
import axios from '../axios'
import Footer from "../components/Footer";


export default class HomeScreen extends Component {
    state = {
        merchandises: []

    };

    componentDidMount() {
        axios
            .get("/api/merchandise")
            .then(data => {
                console.log(data.data);
                this.setState({
                    merchandises : data.data
                });
            })
            .catch(err => console.error(err));
    }
    render() {

        return (
            <div>
                <NavBar
                    username={this.props.username}
                    onLogin={this.props.onLogin}
                />
                <div className="row">

                    <div className="col-3">

                        <span>
                        Introducing Your New Workout Wardrobe
                        </span>
                    </div>
                    <div className="col-9">
                        <img src="https://m.media-amazon.com/images/S/aplus-seller-content-images-us-east-1/ATVPDKIKX0DER/A1TCY5XH41Q5NO/8808b61f-559d-4325-b18d-1624f1ca7c31._CR0,0,970,600_PT0_SX970__.jpg"/>
                    </div>

                </div>

                <Footer/>
            </div>
        )
    }
}
