import React, { Component } from 'react';
import NavBar from "../components/NavBar";
import axios from '../axios'
import Footer from "../components/Footer";


export default class HomeScreen extends Component {
    state = {
        merchandises: []

    };

    componentDidMount() {
        axios
            .get("/api/merchandise")
            .then(data => {
                console.log(data.data);
                this.setState({
                    merchandises : data.data
                });
            })
            .catch(err => console.error(err));
    }
    render() {

        return (
            <div>
                <NavBar
                    username={this.props.username}
                    onLogin={this.props.onLogin}
                />
                <div className="row">
                    <div className="col-4 shopping1">
                        <a>
                            <div className="shopping2">
                                <p className="shopping3" >
                                Introducing Your 
                                New Workout Wardrobe
                                </p>

                                <a href="http://localhost:3000/shop">
                                    <button className="shopping4">
                                        Start Shopping
                                    </button>
                                </a>
                            </div>
                        </a>
                    </div>
                    <div className="col-8 " >
                        <img className="imgHome" src="https://m.media-amazon.com/images/S/aplus-seller-content-images-us-east-1/ATVPDKIKX0DER/A1TCY5XH41Q5NO/8808b61f-559d-4325-b18d-1624f1ca7c31._CR0,0,970,600_PT0_SX970__.jpg"/>
                    </div>
                </div>
                <div>
                    <p></p>
                </div>
                <div className = "row">
                    <div className="col-4" href="/about">
                        <a>

                        </a>
                        <img className="images" src="https://i.imgur.com/ZpDBZOo.png" />
                    </div>
                    <div className="col-4">
                        <img className="images" src="https://i.imgur.com/WnDxNJb.png"/>
                    </div>
                    <div className="col-4">
                        <img className="images" src="https://i.imgur.com/Vy7uovF.png"/>
                    </div>
                </div>
                <Footer/>
            </div>
        )
    }
}
