import React, { Component } from 'react'

import axios from "../axios";

export default class SignUpScreen extends Component {
    state = {
        username: '',
        password: '',
        passwordconfirmation : '',
        email: ''
    }

    _onSignup = (username, email , password ) => {
        axios
          .post("https://unterwasche-api.herokuapp.com/api/auth/user", {
            username: username,
            password: password,
            email : email
          })
          .then(response => {
            if(response.data.success){
              window.localStorage.setItem("access_token", response.data.access_token)
              this.setState({
                username: response.data.user.username,
                id: response.data.id
              });
              this.props.history.push("/");
            }
            else {
              alert(response.data.message);
            }
          })
          .catch(err => console.error(err));
      };


    render() {
        return (
            <div className="clo-4 offset-4 signup" className="body">
                <div className="all">
                    <form onSubmit={this.handleSubmit}>
                        <div className="imgcontainer">
                            <img src="https://instagram.fhan2-2.fna.fbcdn.net/v/t51.2885-15/sh0.08/e35/c0.2.1440.1440a/s640x640/75304581_442855213098634_3506143733081050171_n.jpg?_nc_ht=instagram.fhan2-2.fna.fbcdn.net&_nc_cat=110&_nc_ohc=XfV8xb347pQAX8mBUz3&oh=fd83d00164af455c59774de2d62fac9f&oe=5E93FBC2" alt="Avatar" className="avatar" />
                            <h1>Sign Up</h1>
                            <p>Please fill in this form to create an account.</p>
                        </div>

                        <div className="container">

                            <div className="form-group">
                                <input onChange={this.handleInputChange} className="form-control" name="username" type="text" placeholder="username" required />
                            </div>

                            <div className="form-group">
                                <input onChange={this.handleInputChange} className="form-control" name="email" type="text" placeholder="email" required />
                            </div>

                            <div className="form-group">
                                <input onChange={this.handleInputChange} className="form-control" name="password" type="password" placeholder="password" required />
                            </div>

                            <div className="form-group">
                                <input onChange={this.handleInputChange} className="form-control" name="password" type="password" placeholder="password confirmation" required />
                            </div>
                        </div>

                        <div className="text-center">
                            <button className="btn btn-danger" type="submit">Sign Up</button>
                        </div>

                        {/* <label> <input type="checkbox" checked="checked" name="remember"/> Remember me </label> */}
                    </form>
                </div>
            </div>
        )
    }
}
