import React, { Component } from 'react';
import { PayPalButton } from "react-paypal-button-v2";
import axios from "axios";

import NavBar from "../components/NavBar";
import Footer from "../components/Footer";

export default class CartScreen extends Component {
    state={

    }

    componentDidMount(){
        axios.get("http://localhost:6969/api/cart")
        .then(data => {
            this.setState({mer : data.data.data});
            console.log(this.state.mer);
        })
        .catch(error => console.log(error));
    }

    render() {
        var index = this.state.mer ? this.state.mer.length : "";
        const mer = this.state.mer ? this.state.mer[index-1] : "";
        // const image = index > 0 ? mer.image : "";
        console.log(mer);
        // console.log(image);
        return (
            <div>
                <NavBar
                    username={this.props.username}
                    onLogin={this.props.onLogin}
                />
                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10 text-center">
                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">MY CART</th>
                                    {/* <th scope="col">Mame </th> */}
                                    <th scope="col">Size </th>
                                    <th scope="col">Quantity</th>
                                    <th scope="col">Price </th>
                                    <th scope="col">Total</th>
                                </tr>
                            </thead>

                            <tbody>
                                <tr>
                                    <th scope="col"><img className= "cart-img"
                                        src={index > 0 ? mer.image : ""} /></th>
                                    <th scope="col">{index > 0 ? mer.size : ""}</th>
                                    <th scope="col">{index > 0 ? mer.quantity : ""}</th>
                                    <th scope="col">{index > 0 ? mer.price : ""} </th>
                                    <th scope="col">{index > 0 ? mer.subtotal : ""}</th>
                                </tr>
                            </tbody>
                            <p>{index > 0 ? mer.name : ""}</p>
                        </table>
                    </div>
                </div>
                <div className="text-center">
                    <PayPalButton
                        amount="100"
                        onSuccess={(details, data) => {
                            alert("Transaction completed by " + details.payer.name.given_name);

                            // OPTIONAL: Call your server to save the transaction
                            return fetch("/paypal-transaction-complete", {
                                method: "post",
                                body: JSON.stringify({
                                    orderID: data.orderID
                                })
                            });
                        }}
                    />
                </div>

                <Footer />
            </div>
        )
    }
}
