import React, { Component } from 'react';
import NavBar from "../components/NavBar";
import Footer from "../components/Footer";
import Product from "../components/Product";
import InfoProduct from "../components/InfoProduct";
import axios from "axios";

export default class DetailScreen extends Component {
    state = {

    };

    componentDidMount() {
        axios
            .get(`http://localhost:6969/api/merchandise/${this.props.match.params.merId}`)
            .then(data => {
                this.setState({mer : data.data.data});
            })
            .catch(error => console.log(error));
    }
    render() {
        const description = this.state.mer  ? 
            this.state.mer.description : "";
        return (
            <div >
                <NavBar
                    username={this.props.username}
                    onLogin={this.props.onLogin}
                />
                <div className="main_content container">
                    <div className="row">
                        <div className="col-6">
                            {this.state.mer ? <Product img={this.state.mer} /> :""}
                        </div>
                        <div>
                            {this.state.mer ? <InfoProduct inf={this.state.mer} /> : ""}
                            {description}
                        </div>
                    </div>
                </div>
                <Footer/>
            </div>
        )
    }
}
